// Build a structure containing information about a region.
// Usage: r = new MapGeometry({label:'Andromeda',galactic:[15,-10],longitudes:new Array(0,20,20,0),latitudes:new Array(0,0,-20,-20)});
//	label (string) = Text that appears at position given by galactic
function MapGeometry(input){
	if(input){
		this.el = (input.el) ? input.el : '';
		this.label = (input.label) ? input.label : '';
		this.path = (input.path) ? input.path : '';
		this.open = (input.open) ? input.open : false;
		this.size = (input.size) ? input.size : 1.0;
		this.ra = (input.ra) ? input.ra : 0.0;
		this.dec = (input.dec) ? input.dec : 0.0;
		this.galactic = (input.galactic) ? input.galactic : Equatorial2Galactic(this.ra,this.dec);
		this.longitudes = (typeof input.longitudes=="object" && input.longitudes.length > 0) ? input.longitudes : '';
		this.latitudes = (typeof input.latitudes=="object" && input.latitudes.length > 0) ? input.latitudes : '';
	}
	kml_coord = Galactic2XY(this.galactic[0],this.galactic[1],this.el.mapSize);
	this.x = kml_coord[0];
	this.y = kml_coord[1];
	this.xypath = '';
}

MapGeometry.prototype.updatePath = function(){
	if(this.path){
		var a = this.path.split(' ');
		var newpath = '';
		var pathtype = 'M';
		var types = new Array(a.length);
		var lons = new Array(a.length);
		var lats = new Array(a.length);
		var abslons = new Array(a.length);
		var abslats = new Array(a.length);
		var xy = new Array(a.length);
		var j = a.length;
		var prevpos = new Array(2);
		var longrange = [0,0];
		var dlon = 0;
		// Set some crazy guesses for longitude range
		this.minx = this.el.mapSize;
		this.maxx = -this.el.mapSize;
		var deg2px = this.el.mapSize/360
		var screenx_left = -$(this.el.container+" .chromo_innerDiv").position().left;
		var screenx_right = screenx_left+this.el.wide;
		var prevx;
		var x;

		for(var i = 0; i < j ; i++){

			if(a[i].length == 1) pathtype = a[i];

			types[i] = pathtype;

			if(a[i].length > 1){
				var c = a[i].split(',');			

				// Absolute/relative positions
				lons[i] = parseFloat(c[0]);
				lats[i] = parseFloat(c[1]);

				abslons[i] = lons[i];
				abslats[i] = lats[i];	

				// Check case of pathtype
				if(types[i] == "m" || types[i] == "l" || types[i] == "c"){
					// Relative positions
					abslons[i] = prevpos[0]-abslons[i];
					abslats[i] = prevpos[1]-abslons[i];
					x = prevx+(lons[i]*deg2px);
				}else{
					// Absolute positions
					while(abslons[i] <= -180) abslons[i] += 360;
					while(abslons[i] > 180) abslons[i] -= 360;
					x = ((180-abslons[i]))*deg2px;
				}
				if(x < this.minx) this.minx = x;
				if(x > this.maxx) this.maxx = x;
				prevpos[0] = abslons[i];
				prevpos[1] = abslats[i];
				prevx = x;
			}
		}

		
		var shift = (this.maxx < screenx_left) ? this.el.mapSize : 0;
		shift += (this.minx > screenx_right) ? -this.el.mapSize : 0;

		for(var i = 0; i < j ; i++){
			if(lons[i] && lats[i]){
				if(types[i] != "m" && types[i] != "l" && types[i] != "c"){
					// Absolute
					x = (180-abslons[i])*deg2px;
					y = this.el.mapSize/2-lats[i]*deg2px;
					x += shift;
					xy[i] = [x,y];
				}else{
					// Relative
					xy[i] = [lons[i]*deg2px,lats[i]*deg2px];
				}

				if(types[i] != types[i-1]) newpath += types[i]+' ';
				newpath += xy[i][0]+','+xy[i][1]+' ';
			}else{
				if(types[i]) newpath += types[i]+' ';
			}
		}

//		$('.chromo_info').append(this.label+'='+parseInt(this.minx)+', '+parseInt(centrelon)+', '+parseInt(this.maxx)+'<br />').show()
//		if(this.label == "Vulpecula") $('.chromo_info').append(screenlon_min+','+screenlon_max+'<br />centrexy='+centrexy[0]+','+centrexy[1]).show()
		//+' '+longrange[0]+','+longrange[1]).show()

//		if(this.label == "Andromeda") $('.chromo_info').append(newpath).show()
		
		this.xypath = newpath;
	}
}

Chromoscope.prototype.toggleBoundaries = function(){
	body = (this.container) ? this.container : 'body';
	$('#'+body+'-holder-canvas').toggle();
}

